import { useRef, useEffect, useState } from 'react'
import { useNavigate } from 'react-router-dom'
import { PageContainer, Header } from '../components/layout'
import { Card, Button } from '../components/ui'

export default function LivePractice() {
  const videoRef = useRef(null)
  const canvasRef = useRef(null)
  const [isActive, setIsActive] = useState(false)
  const [feedback, setFeedback] = useState([])
  const [selectedDrill, setSelectedDrill] = useState('smash')
  const navigate = useNavigate()

  useEffect(() => {
    if (isActive) {
      startCamera()
    } else {
      stopCamera()
    }

    return () => stopCamera()
  }, [isActive])

  const startCamera = async () => {
    try {
      const stream = await navigator.mediaDevices.getUserMedia({
        video: { facingMode: 'user' }
      })

      if (videoRef.current) {
        videoRef.current.srcObject = stream
        videoRef.current.play()
      }

      requestAnimationFrame(processPose)
    } catch (error) {
      console.error('Camera access error:', error)
      setFeedback(['Camera access denied. Please allow camera permissions.'])
    }
  }

  const stopCamera = () => {
    if (videoRef.current?.srcObject) {
      const tracks = videoRef.current.srcObject.getTracks()
      tracks.forEach(track => track.stop())
      videoRef.current.srcObject = null
    }
  }

  const processPose = () => {
    if (!isActive || !videoRef.current || !canvasRef.current) return

    const video = videoRef.current
    const canvas = canvasRef.current
    const ctx = canvas.getContext('2d')

    canvas.width = video.videoWidth
    canvas.height = video.videoHeight

    ctx.clearRect(0, 0, canvas.width, canvas.height)

    // Placeholder overlay
    ctx.fillStyle = 'rgba(124, 58, 237, 0.9)'
    ctx.font = 'bold 24px Nunito, sans-serif'
    ctx.fillText('Live Pose Detection (Coming Soon)', 20, 40)
    ctx.font = '18px DM Sans, sans-serif'
    ctx.fillText(`Current Drill: ${selectedDrill}`, 20, 70)

    if (isActive) {
      requestAnimationFrame(processPose)
    }
  }

  const drills = [
    { id: 'smash', name: 'Smash Practice', description: 'Focus on overhead smash form', icon: '💥' },
    { id: 'clear', name: 'Clear Practice', description: 'Practice defensive clears', icon: '🎯' },
    { id: 'netshot', name: 'Net Shot', description: 'Work on net play technique', icon: '🏸' },
    { id: 'footwork', name: 'Footwork', description: 'Improve court movement', icon: '👟' },
  ]

  return (
    <PageContainer>
      <div className="max-w-7xl mx-auto px-4 py-6">
        <Header
          title="Live Practice"
          subtitle="Real-time AR pose correction"
          onBack={() => navigate('/dashboard')}
        />

        <div className="mt-6 grid grid-cols-1 lg:grid-cols-[320px_1fr] gap-6">
          {/* Sidebar */}
          <div className="space-y-4">
            {/* Drill Selection */}
            <Card hover={false} className="p-5">
              <h3 className="font-display font-bold text-text-primary mb-4">
                Select Drill
              </h3>
              <div className="space-y-3">
                {drills.map(drill => (
                  <button
                    key={drill.id}
                    onClick={() => setSelectedDrill(drill.id)}
                    className={`
                      w-full p-4 rounded-[20px] text-left
                      transition-all duration-300
                      ${selectedDrill === drill.id
                        ? 'bg-gradient-to-br from-accent-violet-light to-accent-violet text-white shadow-[var(--shadow-clay-button)] scale-[1.02]'
                        : 'bg-white/60 hover:bg-white/80 text-text-primary hover:-translate-y-0.5'
                      }
                    `}
                  >
                    <div className="flex items-center gap-3">
                      <span className="text-2xl">{drill.icon}</span>
                      <div>
                        <div className="font-display font-bold">{drill.name}</div>
                        <div className={`text-sm mt-0.5 ${
                          selectedDrill === drill.id ? 'text-white/80' : 'text-text-muted'
                        }`}>
                          {drill.description}
                        </div>
                      </div>
                    </div>
                  </button>
                ))}
              </div>
            </Card>

            {/* Start/Stop Button */}
            <Button
              onClick={() => setIsActive(!isActive)}
              variant={isActive ? 'danger' : 'success'}
              size="lg"
              className="w-full"
            >
              {isActive ? (
                <>
                  <svg className="w-5 h-5" fill="none" stroke="currentColor" viewBox="0 0 24 24">
                    <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M21 12a9 9 0 11-18 0 9 9 0 0118 0z" />
                    <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M9 10a1 1 0 011-1h4a1 1 0 011 1v4a1 1 0 01-1 1h-4a1 1 0 01-1-1v-4z" />
                  </svg>
                  Stop Practice
                </>
              ) : (
                <>
                  <svg className="w-5 h-5" fill="none" stroke="currentColor" viewBox="0 0 24 24">
                    <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M14.752 11.168l-3.197-2.132A1 1 0 0010 9.87v4.263a1 1 0 001.555.832l3.197-2.132a1 1 0 000-1.664z" />
                    <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M21 12a9 9 0 11-18 0 9 9 0 0118 0z" />
                  </svg>
                  Start Practice
                </>
              )}
            </Button>

            {/* Feedback Panel */}
            {feedback.length > 0 && (
              <Card hover={false} className="p-5 border-l-4 border-warning bg-warning/5">
                <h4 className="font-display font-bold text-text-primary mb-2">
                  Real-time Feedback
                </h4>
                <ul className="space-y-2">
                  {feedback.map((msg, i) => (
                    <li key={i} className="flex items-start gap-2 text-sm text-text-muted">
                      <span className="text-warning">•</span>
                      {msg}
                    </li>
                  ))}
                </ul>
              </Card>
            )}
          </div>

          {/* Camera View */}
          <Card hover={false} padding={false} className="overflow-hidden">
            <div className="relative bg-text-primary rounded-[32px] min-h-[500px]">
              {/* Video wrapper - preserves all refs and functionality */}
              <div className="relative w-full h-full">
                <video
                  ref={videoRef}
                  className="w-full h-full object-cover scale-x-[-1] rounded-[32px]"
                  autoPlay
                  playsInline
                  muted
                />
                <canvas
                  ref={canvasRef}
                  className="absolute top-0 left-0 w-full h-full pointer-events-none rounded-[32px]"
                />
              </div>

              {/* Placeholder when inactive */}
              {!isActive && (
                <div className="absolute inset-0 flex items-center justify-center">
                  <div className="text-center max-w-md px-6">
                    <div className="
                      w-24 h-24 rounded-[28px] mx-auto mb-6
                      bg-gradient-to-br from-accent-violet-light to-accent-violet
                      flex items-center justify-center
                      shadow-[var(--shadow-clay-button)]
                      animate-breathe
                    ">
                      <svg className="w-12 h-12 text-white" fill="none" stroke="currentColor" viewBox="0 0 24 24">
                        <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M15 10l4.553-2.276A1 1 0 0121 8.618v6.764a1 1 0 01-1.447.894L15 14M5 18h8a2 2 0 002-2V8a2 2 0 00-2-2H5a2 2 0 00-2 2v8a2 2 0 002 2z" />
                      </svg>
                    </div>
                    <h2 className="text-2xl font-display font-black text-white mb-3">
                      Real-Time AR Pose Correction
                    </h2>
                    <p className="text-white/70 mb-4">
                      Select a drill and click "Start Practice" to begin
                    </p>
                    <div className="
                      inline-flex items-center gap-2 px-4 py-2
                      bg-white/10 rounded-full
                      text-white/80 text-sm
                    ">
                      <svg className="w-4 h-4" fill="none" stroke="currentColor" viewBox="0 0 24 24">
                        <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M15 10l4.553-2.276A1 1 0 0121 8.618v6.764a1 1 0 01-1.447.894L15 14M5 18h8a2 2 0 002-2V8a2 2 0 00-2-2H5a2 2 0 00-2 2v8a2 2 0 002 2z" />
                      </svg>
                      Uses your camera for live feedback
                    </div>
                  </div>
                </div>
              )}

              {/* Active indicator */}
              {isActive && (
                <div className="absolute top-4 left-4 flex items-center gap-2 px-3 py-1.5 bg-error/90 rounded-full">
                  <span className="w-2 h-2 bg-white rounded-full animate-pulse" />
                  <span className="text-white text-sm font-medium">LIVE</span>
                </div>
              )}
            </div>
          </Card>
        </div>

        {/* Instructions */}
        <Card hover={false} className="mt-6 max-w-3xl mx-auto">
          <h4 className="font-display font-bold text-text-primary mb-4">
            How it works
          </h4>
          <div className="grid grid-cols-1 sm:grid-cols-5 gap-4">
            {[
              { step: '1', text: 'Select a drill type' },
              { step: '2', text: 'Click "Start Practice"' },
              { step: '3', text: 'Perform movement slowly' },
              { step: '4', text: 'Watch color indicators' },
              { step: '5', text: 'Follow feedback' },
            ].map((item, i) => (
              <div key={i} className="flex items-center gap-3 sm:flex-col sm:text-center">
                <div className="
                  w-10 h-10 rounded-full flex-shrink-0
                  bg-gradient-to-br from-accent-violet-light to-accent-violet
                  flex items-center justify-center
                  text-white font-display font-bold
                  shadow-[var(--shadow-clay-button)]
                ">
                  {item.step}
                </div>
                <span className="text-sm text-text-muted">{item.text}</span>
              </div>
            ))}
          </div>
          <div className="mt-6 pt-4 border-t border-white/40 flex items-center gap-3">
            <div className="flex gap-2">
              <span className="w-4 h-4 rounded-full bg-accent-green" />
              <span className="text-sm text-text-muted">Good form</span>
            </div>
            <div className="flex gap-2">
              <span className="w-4 h-4 rounded-full bg-error" />
              <span className="text-sm text-text-muted">Needs improvement</span>
            </div>
          </div>
        </Card>
      </div>
    </PageContainer>
  )
}

