import { useLocation, useNavigate } from 'react-router-dom'
import { useState, useRef } from 'react'

export default function ResultsEnhanced() {
  const location = useLocation()
  const navigate = useNavigate()

  // Robust data accessing
  const data = location.state?.analysis || {}
  const { shots, gemini_feedback, annotated_video_url } = data
  // Use gemini_feedback if available, otherwise fallback to feedback (for backwards compatibility)
  const feedback = gemini_feedback || data.feedback || {}

  const videoRef = useRef(null)
  const [currentFrame, setCurrentFrame] = useState(0)
  const [showGhost, setShowGhost] = useState(false) // Toggle state ready for logic
  const [videoError, setVideoError] = useState(null)

  // Debug: Log video URL
  console.log('Video URL:', annotated_video_url)
  console.log('Shots count:', shots?.length)
  console.log('Feedback:', feedback)

  // Handle case where user navigates directly without data
  if (!shots && !annotated_video_url) {
    return (
      <div style={styles.container}>
        <div style={styles.card}>
          <h2>No Analysis Data Found</h2>
          <button onClick={() => navigate('/')} style={styles.button}>Upload a Video</button>
        </div>
      </div>
    )
  }

  // Find current shot based on frame window (± 15 frames)
  const currentShot = shots?.find(shot => Math.abs(shot.frame - currentFrame) < 15)

  const handleTimeUpdate = () => {
    if (videoRef.current) {
      // Assuming 30fps for calculation
      setCurrentFrame(Math.floor(videoRef.current.currentTime * 30))
    }
  }

  const jumpToFrame = (frameNumber) => {
    if (videoRef.current) {
      videoRef.current.currentTime = frameNumber / 30
      videoRef.current.play()
    }
  }

  return (
    <div style={styles.container}>
      <div style={styles.header}>
        <h1 style={styles.title}>🏸 Match Analysis</h1>
        <button onClick={() => navigate('/')} style={styles.backButton}>New Upload</button>
      </div>

      <div style={styles.content}>

        {/* VIDEO SECTION */}
        <div style={styles.card}>
          <h2 style={styles.sectionTitle}>📹 Annotated Video Playback</h2>
          <p style={{color: '#888', fontSize: '0.9rem', marginBottom: '10px'}}>
            Watch your performance with real-time skeleton overlay and shot markers
          </p>
          {!annotated_video_url && (
            <div style={{padding: '20px', backgroundColor: '#ff000020', borderRadius: '8px', marginBottom: '10px'}}>
              ⚠️ Video URL not available. Check backend logs.
            </div>
          )}
          {videoError && (
            <div style={{padding: '20px', backgroundColor: '#ff000020', borderRadius: '8px', marginBottom: '10px'}}>
              ⚠️ Video failed to load: {videoError}
            </div>
          )}
          <div style={styles.videoContainer}>
            <video
              ref={videoRef}
              src={annotated_video_url}
              controls
              style={styles.video}
              onTimeUpdate={handleTimeUpdate}
              onError={(e) => {
                console.error('Video error:', e)
                setVideoError('Failed to load video. Check if file exists and server is running.')
              }}
              onLoadedMetadata={() => {
                console.log('✓ Video loaded successfully')
                setVideoError(null)
              }}
            />

            {/* Real-time Overlay on top of Video */}
            {currentShot && (
              <div style={styles.shotOverlay}>
                <h3 style={{margin:0, color:'#4CAF50'}}>{currentShot.shot_type || currentShot.played}</h3>
                {/* Show tactical advice if exists */}
                {currentShot.suggestion && (
                   <div style={styles.tacticBox}>
                     <span style={{color: '#aaa', fontSize: '0.8rem'}}>COACH SUGGESTION:</span><br/>
                     {currentShot.suggestion[0]?.shot || "Nice Shot"}
                   </div>
                )}
              </div>
            )}
          </div>

          <div style={styles.videoControls}>
            <span>Frame: {currentFrame}</span>
            <label style={styles.controlLabel}>
                <input type="checkbox" checked={showGhost} onChange={e => setShowGhost(e.target.checked)} />
                <span style={{marginLeft: '8px'}}>Compare with Pro Ghost</span>
            </label>
          </div>
        </div>

        {/* FEEDBACK SECTION */}
        <div style={styles.grid}>
            {/* Left: Shot List */}
            <div style={styles.card}>
                <h2 style={styles.sectionTitle}>Shot Timeline</h2>
                <div style={styles.scrollList}>
                    {shots?.map((shot, i) => (
                        <div key={i} style={styles.shotItem} onClick={() => jumpToFrame(shot.frame)}>
                            <div style={{display:'flex', justifyContent:'space-between'}}>
                                <strong>{shot.played || shot.shot_type}</strong>
                                <span style={styles.frameLabel}>F: {shot.frame}</span>
                            </div>
                            {/* Logic: If prediction matches played, it's good. Else tactic error */}
                            <div style={{fontSize: '0.85rem', marginTop: '5px', color: '#555'}}>
                                Suggestion: {shot.suggestion ? shot.suggestion[0].shot : 'N/A'}
                            </div>
                        </div>
                    ))}
                </div>
            </div>

            {/* Right: AI Report */}
            <div style={styles.card}>
                <h2 style={styles.sectionTitle}>Gemini Coach Report</h2>
                <div style={styles.feedbackSection}>
                    <h3>Technique Analysis</h3>
                    <p>{feedback?.technique || "Analyzing form..."}</p>

                    <h3 style={{marginTop: '20px'}}>Training Plan</h3>
                    <ul style={styles.trainingList}>
                        {feedback?.training_plan ? (
                            // Handle if it's a string (markdown) or array
                            typeof feedback.training_plan === 'string' 
                            ? feedback.training_plan.split('\n').map((line, k) => <li key={k}>{line}</li>)
                            : feedback.training_plan.map((item, k) => <li key={k} style={styles.trainingItem}>{item}</li>)
                        ) : <li>No specific plan generated.</li>}
                    </ul>
                </div>
            </div>
        </div>

      </div>
    </div>
  )
}

// Keeping your Styles object mostly consistent, adding Grid support
const styles = {
  container: { minHeight: '100vh', backgroundColor: '#1a1a1a', color: 'white', padding: '2rem' },
  header: { display: 'flex', justifyContent: 'space-between', marginBottom: '2rem' },
  title: { margin: 0, color: '#4CAF50' },
  backButton: { padding: '0.5rem 1rem', backgroundColor: '#333', color: 'white', border: 'none', borderRadius: '4px', cursor: 'pointer' },
  content: { maxWidth: '1200px', margin: '0 auto', display: 'flex', flexDirection: 'column', gap: '1.5rem' },
  card: { backgroundColor: '#2d2d2d', padding: '1.5rem', borderRadius: '8px', boxShadow: '0 4px 6px rgba(0,0,0,0.3)' },
  grid: { display: 'grid', gridTemplateColumns: '1fr 1fr', gap: '1.5rem' },
  sectionTitle: { marginTop: 0, borderBottom: '1px solid #444', paddingBottom: '10px' },
  videoContainer: { position: 'relative', backgroundColor: 'black', borderRadius: '8px', overflow: 'hidden', aspectRatio: '16/9' },
  video: { width: '100%', height: '100%', objectFit: 'contain' },
  shotOverlay: { position: 'absolute', top: 20, right: 20, backgroundColor: 'rgba(0,0,0,0.8)', padding: '15px', borderRadius: '8px', borderLeft: '4px solid #4CAF50' },
  tacticBox: { marginTop: '5px', fontSize: '1.1rem', fontWeight: 'bold' },
  videoControls: { display: 'flex', justifyContent: 'space-between', marginTop: '10px', color: '#888' },
  scrollList: { maxHeight: '400px', overflowY: 'auto' },
  shotItem: { padding: '10px', borderBottom: '1px solid #444', cursor: 'pointer', transition: 'background 0.2s' },
  frameLabel: { backgroundColor: '#444', padding: '2px 6px', borderRadius: '4px', fontSize: '0.7rem' },
  trainingList: { paddingLeft: '20px' },
  trainingItem: { marginBottom: '8px', color: '#ddd' },
  button: { padding: '10px 20px', backgroundColor: '#4CAF50', color: 'white', border: 'none', borderRadius: '4px', fontSize: '1rem', cursor: 'pointer' }
}

