import { forwardRef } from 'react'

const Input = forwardRef(({
  label,
  error,
  className = '',
  ...props
}, ref) => {
  return (
    <div className="flex flex-col gap-2">
      {label && (
        <label className="text-sm font-medium text-text-muted font-display">
          {label}
        </label>
      )}
      <input
        ref={ref}
        className={`
          w-full px-5 py-4
          bg-white/60 backdrop-blur-xl
          border-2 border-white/40
          rounded-[20px]
          text-text-primary placeholder:text-text-light
          shadow-[var(--shadow-clay-input)]
          transition-all duration-200
          focus:outline-none focus:bg-white focus:border-accent-violet/40
          focus:shadow-[var(--shadow-clay-input-focus)]
          disabled:opacity-50 disabled:cursor-not-allowed
          ${error ? 'border-error/50 focus:border-error' : ''}
          ${className}
        `}
        {...props}
      />
      {error && (
        <span className="text-sm text-error font-medium">{error}</span>
      )}
    </div>
  )
})

Input.displayName = 'Input'

export default Input

const Badge = ({
  children,
  variant = 'violet',
  size = 'md',
  className = '',
  ...props
}) => {
  const variants = {
    violet: 'bg-accent-violet/15 text-accent-violet border-accent-violet/30',
    pink: 'bg-accent-pink/15 text-accent-pink border-accent-pink/30',
    blue: 'bg-accent-blue/15 text-accent-blue border-accent-blue/30',
    green: 'bg-accent-green/15 text-accent-green border-accent-green/30',
    amber: 'bg-accent-amber/15 text-accent-amber border-accent-amber/30',
    success: 'bg-success/15 text-success border-success/30',
    warning: 'bg-warning/15 text-warning border-warning/30',
    error: 'bg-error/15 text-error border-error/30',
    neutral: 'bg-text-muted/10 text-text-muted border-text-muted/20',
  }

  const sizes = {
    sm: 'px-2 py-0.5 text-xs',
    md: 'px-3 py-1 text-sm',
    lg: 'px-4 py-1.5 text-base',
  }

  return (
    <span
      className={`
        inline-flex items-center
        font-medium font-display
        rounded-full
        border
        whitespace-nowrap
        ${variants[variant]}
        ${sizes[size]}
        ${className}
      `}
      {...props}
    >
      {children}
    </span>
  )
}

export default Badge

const Progress = ({
  value = 0,
  max = 100,
  label,
  showPercent = true,
  variant = 'violet',
  size = 'md',
  className = '',
}) => {
  const percentage = Math.min(100, Math.max(0, Math.round((value / max) * 100)))

  const colors = {
    violet: 'bg-gradient-to-r from-accent-violet-light to-accent-violet',
    pink: 'bg-gradient-to-r from-accent-pink-light to-accent-pink',
    blue: 'bg-gradient-to-r from-accent-blue-light to-accent-blue',
    green: 'bg-gradient-to-r from-accent-green-light to-accent-green',
    amber: 'bg-gradient-to-r from-accent-amber-light to-accent-amber',
  }

  const sizes = {
    sm: 'h-2',
    md: 'h-3',
    lg: 'h-4',
  }

  return (
    <div className={`flex flex-col gap-2 ${className}`}>
      {(label || showPercent) && (
        <div className="flex justify-between text-sm">
          {label && <span className="text-text-muted font-medium">{label}</span>}
          {showPercent && (
            <span className="font-bold text-text-primary font-display">
              {percentage}%
            </span>
          )}
        </div>
      )}
      <div className={`
        ${sizes[size]}
        bg-white/40
        rounded-full
        overflow-hidden
        shadow-[var(--shadow-clay-input)]
      `}>
        <div
          className={`
            h-full
            rounded-full
            transition-all duration-500 ease-out
            ${colors[variant]}
          `}
          style={{ width: `${percentage}%` }}
        />
      </div>
    </div>
  )
}

export default Progress

import FloatingBlobs from './FloatingBlobs'

const PageContainer = ({
  children,
  showBlobs = true,
  centered = false,
  className = '',
}) => {
  return (
    <div className={`
      min-h-screen
      bg-canvas
      relative
      ${centered ? 'flex items-center justify-center' : ''}
      ${className}
    `}>
      {showBlobs && <FloatingBlobs />}
      <div className="relative z-10 w-full">
        {children}
      </div>
    </div>
  )
}

export default PageContainer

import { Button } from '../ui'

const Header = ({
  title,
  subtitle,
  actions,
  onBack,
  backLabel = 'Back',
  className = '',
}) => {
  return (
    <header className={`
      flex flex-wrap items-start justify-between gap-4
      px-4 md:px-8 py-6
      ${className}
    `}>
      <div className="flex items-center gap-4">
        {onBack && (
          <Button
            variant="ghost"
            size="sm"
            onClick={onBack}
            className="text-text-muted hover:text-accent-violet"
          >
            <svg
              className="w-5 h-5"
              fill="none"
              stroke="currentColor"
              viewBox="0 0 24 24"
            >
              <path
                strokeLinecap="round"
                strokeLinejoin="round"
                strokeWidth={2}
                d="M15 19l-7-7 7-7"
              />
            </svg>
            {backLabel}
          </Button>
        )}
        <div>
          <h1 className="text-2xl md:text-3xl font-display font-black text-text-primary">
            {title}
          </h1>
          {subtitle && (
            <p className="mt-1 text-text-muted">{subtitle}</p>
          )}
        </div>
      </div>
      {actions && (
        <div className="flex flex-wrap gap-3">
          {actions}
        </div>
      )}
    </header>
  )
}

export default Header

import { useLanguage } from '../context/LanguageContext'

export default function LanguageSelector() {
  const { language, changeLanguage, availableLanguages } = useLanguage()

  return (
    <div style={styles.container}>
      <select
        value={language}
        onChange={(e) => changeLanguage(e.target.value)}
        style={styles.select}
      >
        {availableLanguages.map(lang => (
          <option key={lang.code} value={lang.code}>
            {lang.name}
          </option>
        ))}
      </select>
    </div>
  )
}

const styles = {
  container: {
    display: 'inline-block',
  },
  select: {
    padding: '0.5rem 1rem',
    borderRadius: '4px',
    border: '1px solid #ddd',
    backgroundColor: 'white',
    cursor: 'pointer',
    fontSize: '0.9rem',
  },
}

