import { useState, useEffect } from 'react'
import { useNavigate } from 'react-router-dom'
import { supabase } from '../lib/supabase'
import { useAuth } from '../context/AuthContext'

export default function Dashboard() {
  const [analyses, setAnalyses] = useState([])
  const [loading, setLoading] = useState(true)
  const [error, setError] = useState('')

  const { user, signOut } = useAuth()
  const navigate = useNavigate()

  useEffect(() => {
    if (user) {
      fetchAnalyses()
    }
  }, [user])

  const fetchAnalyses = async () => {
    try {
      setLoading(true)
      const { data, error } = await supabase
        .from('analysis_results')
        .select('*')
        .eq('user_id', user.id)
        .order('created_at', { ascending: false })

      if (error) throw error

      setAnalyses(data || [])
    } catch (err) {
      console.error('Error fetching analyses:', err)
      setError('Failed to load your video history')
    } finally {
      setLoading(false)
    }
  }

  const handleDelete = async (id) => {
    if (!confirm('Delete this analysis?')) return

    try {
      const { error } = await supabase
        .from('analysis_results')
        .delete()
        .eq('id', id)

      if (error) throw error

      setAnalyses(analyses.filter(a => a.id !== id))
    } catch (err) {
      console.error('Error deleting:', err)
      alert('Failed to delete analysis')
    }
  }

  const handleSignOut = async () => {
    await signOut()
    navigate('/')
  }

  const formatDate = (dateString) => {
    const date = new Date(dateString)
    return date.toLocaleDateString() + ' ' + date.toLocaleTimeString()
  }

  return (
    <div style={styles.container}>
      {/* Header */}
      <div style={styles.header}>
        <div>
          <h1 style={styles.title}>My Videos</h1>
          <p style={styles.subtitle}>
            {user?.email} • {analyses.length} analysis{analyses.length !== 1 ? 'es' : ''}
          </p>
        </div>
        <div style={styles.headerButtons}>
          <button onClick={() => navigate('/upload')} style={styles.uploadButton}>
            + Upload New Video
          </button>
          <button onClick={() => navigate('/live-practice')} style={styles.liveButton}>
            🎯 Live Practice
          </button>
          <button onClick={handleSignOut} style={styles.signOutButton}>
            Sign Out
          </button>
        </div>
      </div>

      {/* Content */}
      <div style={styles.content}>
        {loading ? (
          <div style={styles.loading}>Loading your videos...</div>
        ) : error ? (
          <div style={styles.error}>{error}</div>
        ) : analyses.length === 0 ? (
