import { useState } from 'react'
import { useNavigate } from 'react-router-dom'
import { useAuth } from '../context/AuthContext'

const BACKEND_URL = import.meta.env.VITE_BACKEND_URL || 'http://localhost:8000'

export default function Upload() {
  const [videoFile, setVideoFile] = useState(null)
  const [uploading, setUploading] = useState(false)
  const [progress, setProgress] = useState('')

  const { user, signOut } = useAuth()
  const navigate = useNavigate()

  const handleFileChange = (e) => {
    const file = e.target.files[0]
    if (file) {
      setVideoFile(file)
    }
  }

  const handleAnalyze = async () => {
    if (!videoFile) return

    setUploading(true)
    setProgress('Uploading video...')

    try {
      // Create FormData
      const formData = new FormData()
      formData.append('file', videoFile)
      if (user) {
        formData.append('user_id', user.id)
      }

      setProgress('Analyzing video (this may take a minute)...')

      // Call backend API
      const response = await fetch(`${BACKEND_URL}/api/analyze`, {
        method: 'POST',
        body: formData,
      })

      if (!response.ok) {
        const errorData = await response.json()
        throw new Error(errorData.detail || 'Analysis failed')
      }

      const resultData = await response.json()
      setProgress('Analysis complete!')

      // Navigate to results page
      navigate('/results', { state: { analysis: resultData } })
    } catch (error) {
      alert('Analysis failed: ' + error.message)
      setProgress('')
    } finally {
      setUploading(false)
    }
  }

  const handleSignOut = async () => {
    await signOut()
    navigate('/')
  }

  return (
    <div style={styles.container}>
      <div style={styles.header}>
        <h1 style={styles.title}>Badminton Video Analyzer</h1>
        {user && (
          <button onClick={handleSignOut} style={styles.signOutButton}>
            Sign Out
          </button>
        )}
      </div>

      <div style={styles.card}>
        <h2 style={styles.subtitle}>Upload a Badminton Video</h2>
        <p style={styles.description}>
          Upload your badminton video to get AI-powered coaching feedback on your technique
        </p>

        <input
          type="file"
          accept="video/*"
          onChange={handleFileChange}
          style={styles.fileInput}
          disabled={uploading}
        />

        {videoFile && (
          <p style={styles.fileName}>Selected: {videoFile.name}</p>
        )}

        {progress && (
          <p style={styles.progress}>{progress}</p>
        )}

        <button
          onClick={handleAnalyze}
          disabled={!videoFile || uploading}
          style={{
            ...styles.button,
            opacity: !videoFile || uploading ? 0.5 : 1,
            cursor: !videoFile || uploading ? 'not-allowed' : 'pointer',
          }}
        >
          {uploading ? 'Analyzing...' : 'Upload & Analyze'}
        </button>
      </div>
    </div>
  )
}

