import { useRef, useEffect, useState } from 'react'
import { useNavigate } from 'react-router-dom'

// This would need MediaPipe or TensorFlow.js for browser-based pose detection
// For now, this is a placeholder structure
export default function LivePractice() {
  const videoRef = useRef(null)
  const canvasRef = useRef(null)
  const [isActive, setIsActive] = useState(false)
  const [feedback, setFeedback] = useState([])
  const [selectedDrill, setSelectedDrill] = useState('smash')
  const navigate = useNavigate()

  useEffect(() => {
    if (isActive) {
      startCamera()
    } else {
      stopCamera()
    }

    return () => stopCamera()
  }, [isActive])

  const startCamera = async () => {
    try {
      const stream = await navigator.mediaDevices.getUserMedia({
        video: { facingMode: 'user' }
      })

      if (videoRef.current) {
        videoRef.current.srcObject = stream
        videoRef.current.play()
      }

      // Start pose detection loop
      requestAnimationFrame(processPose)
    } catch (error) {
      console.error('Camera access error:', error)
      setFeedback(['Camera access denied. Please allow camera permissions.'])
    }
  }

  const stopCamera = () => {
    if (videoRef.current?.srcObject) {
      const tracks = videoRef.current.srcObject.getTracks()
      tracks.forEach(track => track.stop())
      videoRef.current.srcObject = null
    }
  }

  const processPose = () => {
    if (!isActive || !videoRef.current || !canvasRef.current) return

    const video = videoRef.current
    const canvas = canvasRef.current
    const ctx = canvas.getContext('2d')

    // Set canvas size to match video
    canvas.width = video.videoWidth
    canvas.height = video.videoHeight

    // Clear canvas
    ctx.clearRect(0, 0, canvas.width, canvas.height)

    // TODO: Add actual pose estimation here with MediaPipe Web or TensorFlow.js
    // For now, show placeholder text
    ctx.fillStyle = 'rgba(255, 255, 255, 0.8)'
    ctx.font = '20px Arial'
    ctx.fillText('Live Pose Detection (Coming Soon)', 20, 40)
    ctx.fillText(`Current Drill: ${selectedDrill}`, 20, 70)

    // Continue loop
    if (isActive) {
      requestAnimationFrame(processPose)
    }
  }

  const drills = [
    { id: 'smash', name: 'Smash Practice', description: 'Focus on overhead smash form' },
    { id: 'clear', name: 'Clear Practice', description: 'Practice defensive clears' },
    { id: 'netshot', name: 'Net Shot', description: 'Work on net play technique' },
    { id: 'footwork', name: 'Footwork', description: 'Improve court movement' },
  ]

  return (
    <div style={styles.container}>
      <div style={styles.header}>
        <h1 style={styles.title}>Live Practice Mode</h1>
        <button onClick={() => navigate('/upload')} style={styles.backButton}>
          Back
        </button>
      </div>

      <div style={styles.content}>
        {/* Drill Selection */}
        <div style={styles.sidebar}>
          <h3 style={styles.sidebarTitle}>Select Drill</h3>
          {drills.map(drill => (
            <div
              key={drill.id}
              style={{
                ...styles.drillCard,
                ...(selectedDrill === drill.id ? styles.drillCardActive : {})
              }}
              onClick={() => setSelectedDrill(drill.id)}
            >
              <strong>{drill.name}</strong>
              <p style={styles.drillDesc}>{drill.description}</p>
            </div>
          ))}

          <div style={styles.controls}>
            <button
              onClick={() => setIsActive(!isActive)}
              style={{
                ...styles.button,
                backgroundColor: isActive ? '#f44336' : '#4CAF50'
              }}
            >
              {isActive ? 'Stop Practice' : 'Start Practice'}
            </button>
          </div>

          {/* Feedback Panel */}
          {feedback.length > 0 && (
            <div style={styles.feedbackPanel}>
              <h4>Real-time Feedback:</h4>
              <ul style={styles.feedbackList}>
                {feedback.map((msg, i) => (
                  <li key={i}>{msg}</li>
                ))}
              </ul>
            </div>
          )}
        </div>

        {/* Camera View */}
        <div style={styles.cameraContainer}>
          <div style={styles.videoWrapper}>
            <video
              ref={videoRef}
              style={styles.video}
              autoPlay
              playsInline
              muted
            />
            <canvas
              ref={canvasRef}
              style={styles.canvas}
            />
          </div>

          {!isActive && (
            <div style={styles.placeholder}>
              <h2>Real-Time AR Pose Correction</h2>
              <p>Select a drill and click "Start Practice" to begin</p>
              <p style={styles.note}>
                 This feature uses your camera to provide live feedback on your form
              </p>
            </div>
          )}
        </div>
      </div>

      <div style={styles.instructions}>
        <h4>How it works:</h4>
        <ol>
          <li>Select a drill type from the left</li>
          <li>Click "Start Practice" to activate your camera</li>
          <li>Perform the movement slowly</li>
          <li>Watch for green (good) and red (needs improvement) indicators</li>
          <li>Follow the real-time feedback messages</li>
        </ol>
      </div>
    </div>
  )
}

