import { useState, useEffect } from 'react'
import { useNavigate } from 'react-router-dom'
import { supabase } from '../lib/supabase'
import { useAuth } from '../context/AuthContext'

export default function Dashboard() {
  const [analyses, setAnalyses] = useState([])
  const [loading, setLoading] = useState(true)
  const [error, setError] = useState('')

  const { user, signOut } = useAuth()
  const navigate = useNavigate()

  useEffect(() => {
    if (user) {
      fetchAnalyses()
    }
  }, [user])

  const fetchAnalyses = async () => {
    try {
      setLoading(true)
      const { data, error } = await supabase
        .from('analysis_results')
        .select('*')
        .eq('user_id', user.id)
        .order('created_at', { ascending: false })

      if (error) throw error

      setAnalyses(data || [])
    } catch (err) {
      console.error('Error fetching analyses:', err)
      setError('Failed to load your video history')
    } finally {
      setLoading(false)
    }
  }

  const handleDelete = async (id) => {
    if (!confirm('Delete this analysis?')) return

    try {
      const { error } = await supabase
        .from('analysis_results')
        .delete()
        .eq('id', id)

      if (error) throw error

      setAnalyses(analyses.filter(a => a.id !== id))
    } catch (err) {
      console.error('Error deleting:', err)
      alert('Failed to delete analysis')
    }
  }

  const handleSignOut = async () => {
    await signOut()
    navigate('/')
  }

  const formatDate = (dateString) => {
    const date = new Date(dateString)
    return date.toLocaleDateString() + ' ' + date.toLocaleTimeString()
  }

  return (
    <div style={styles.container}>
      {/* Header */}
      <div style={styles.header}>
        <div>
          <h1 style={styles.title}>My Videos</h1>
          <p style={styles.subtitle}>
            {user?.email} â€¢ {analyses.length} analysis{analyses.length !== 1 ? 'es' : ''}
          </p>
        </div>
        <div style={styles.headerButtons}>
          <button onClick={() => navigate('/upload')} style={styles.uploadButton}>
            + Upload New Video
          </button>
          <button onClick={() => navigate('/live-practice')} style={styles.liveButton}>
            ðŸŽ¯ Live Practice
          </button>
          <button onClick={handleSignOut} style={styles.signOutButton}>
            Sign Out
          </button>
        </div>
      </div>

      {/* Content */}
      <div style={styles.content}>
        {loading ? (
          <div style={styles.loading}>Loading your videos...</div>
        ) : error ? (
          <div style={styles.error}>{error}</div>
        ) : analyses.length === 0 ? (
          <div style={styles.empty}>
            <h2>No videos yet</h2>
            <p>Upload your first badminton video to get started!</p>
            <button onClick={() => navigate('/upload')} style={styles.uploadButton}>
              Upload Video
            </button>
          </div>
        ) : (
          <div style={styles.grid}>
            {analyses.map((analysis) => (
              <div key={analysis.id} style={styles.card}>
                <div style={styles.cardHeader}>
                  <h3 style={styles.cardTitle}>
                    Analysis #{analysis.id.slice(0, 8)}
                  </h3>
                  <span style={styles.cardDate}>
                    {formatDate(analysis.created_at)}
                  </span>
                </div>

                <div style={styles.cardBody}>
                  {analysis.result_json?.shots && (
                    <div style={styles.stat}>
                      <span style={styles.statLabel}>Shots Detected:</span>
                      <span style={styles.statValue}>
                        {analysis.result_json.shots.length}
                      </span>
                    </div>
                  )}

                  {analysis.result_json?.shots && (
                    <div style={styles.shotTypes}>
                      {analysis.result_json.shots.map((shot, idx) => (
                        <span
                          key={idx}
                          style={{
                            ...styles.shotBadge,
                            backgroundColor: shot.pose_errors?.length > 0
                              ? '#ffebee'
                              : '#e8f5e9'
                          }}
                        >
                          {shot.shot_type}
                        </span>
                      ))}
                    </div>
                  )}

                  {analysis.result_json?.feedback && (
                    <div style={styles.preview}>
                      <p style={styles.previewText}>
                        {analysis.result_json.feedback.technique?.slice(0, 100) ||
                         analysis.result_json.feedback?.slice(0, 100)}
                        {(analysis.result_json.feedback.technique?.length > 100 ||
                          analysis.result_json.feedback?.length > 100) && '...'}
                      </p>
                    </div>
                  )}
                </div>

                <div style={styles.cardFooter}>
                  <button
                    onClick={() => navigate('/results', {
                      state: { analysis: analysis.result_json }
                    })}
                    style={styles.viewButton}
                  >
                    View Details
                  </button>
                  <button
                    onClick={() => handleDelete(analysis.id)}
                    style={styles.deleteButton}
                  >
                    Delete
                  </button>
                </div>
              </div>
            ))}
          </div>
        )}
      </div>
    </div>
  )
}

