import { useRef, useEffect, useState } from 'react'
import { useNavigate } from 'react-router-dom'

// This would need MediaPipe or TensorFlow.js for browser-based pose detection
// For now, this is a placeholder structure
export default function LivePractice() {
  const videoRef = useRef(null)
  const canvasRef = useRef(null)
  const [isActive, setIsActive] = useState(false)
  const [feedback, setFeedback] = useState([])
  const [selectedDrill, setSelectedDrill] = useState('smash')
  const navigate = useNavigate()

  useEffect(() => {
    if (isActive) {
      startCamera()
    } else {
      stopCamera()
    }

    return () => stopCamera()
  }, [isActive])

  const startCamera = async () => {
    try {
      const stream = await navigator.mediaDevices.getUserMedia({
        video: { facingMode: 'user' }
      })

      if (videoRef.current) {
        videoRef.current.srcObject = stream
        videoRef.current.play()
      }

      // Start pose detection loop
      requestAnimationFrame(processPose)
    } catch (error) {
      console.error('Camera access error:', error)
      setFeedback(['Camera access denied. Please allow camera permissions.'])
    }
  }

  const stopCamera = () => {
    if (videoRef.current?.srcObject) {
      const tracks = videoRef.current.srcObject.getTracks()
      tracks.forEach(track => track.stop())
      videoRef.current.srcObject = null
    }
  }

  const processPose = () => {
    if (!isActive || !videoRef.current || !canvasRef.current) return

    const video = videoRef.current
    const canvas = canvasRef.current
    const ctx = canvas.getContext('2d')

    // Set canvas size to match video
    canvas.width = video.videoWidth
    canvas.height = video.videoHeight

    // Clear canvas
    ctx.clearRect(0, 0, canvas.width, canvas.height)

    // TODO: Add actual pose estimation here with MediaPipe Web or TensorFlow.js
    // For now, show placeholder text
    ctx.fillStyle = 'rgba(255, 255, 255, 0.8)'
    ctx.font = '20px Arial'
    ctx.fillText('Live Pose Detection (Coming Soon)', 20, 40)
    ctx.fillText(`Current Drill: ${selectedDrill}`, 20, 70)

    // Continue loop
    if (isActive) {
      requestAnimationFrame(processPose)
    }
  }

  const drills = [
    { id: 'smash', name: 'Smash Practice', description: 'Focus on overhead smash form' },
    { id: 'clear', name: 'Clear Practice', description: 'Practice defensive clears' },
    { id: 'netshot', name: 'Net Shot', description: 'Work on net play technique' },
    { id: 'footwork', name: 'Footwork', description: 'Improve court movement' },
  ]

  return (
    <div style={styles.container}>
      <div style={styles.header}>
        <h1 style={styles.title}>Live Practice Mode</h1>
        <button onClick={() => navigate('/upload')} style={styles.backButton}>
          Back
        </button>
      </div>

      <div style={styles.content}>
        {/* Drill Selection */}
        <div style={styles.sidebar}>
          <h3 style={styles.sidebarTitle}>Select Drill</h3>
          {drills.map(drill => (
            <div
              key={drill.id}
              style={{
                ...styles.drillCard,
                ...(selectedDrill === drill.id ? styles.drillCardActive : {})
              }}
              onClick={() => setSelectedDrill(drill.id)}
            >
              <strong>{drill.name}</strong>
              <p style={styles.drillDesc}>{drill.description}</p>
            </div>
          ))}

          <div style={styles.controls}>
            <button
              onClick={() => setIsActive(!isActive)}
              style={{
                ...styles.button,
                backgroundColor: isActive ? '#f44336' : '#4CAF50'
              }}
            >
              {isActive ? 'Stop Practice' : 'Start Practice'}
            </button>
          </div>

          {/* Feedback Panel */}
          {feedback.length > 0 && (
            <div style={styles.feedbackPanel}>
              <h4>Real-time Feedback:</h4>
              <ul style={styles.feedbackList}>
                {feedback.map((msg, i) => (
                  <li key={i}>{msg}</li>
                ))}
              </ul>
            </div>
          )}
        </div>

        {/* Camera View */}
        <div style={styles.cameraContainer}>
          <div style={styles.videoWrapper}>
            <video
              ref={videoRef}
              style={styles.video}
              autoPlay
              playsInline
              muted
            />
            <canvas
              ref={canvasRef}
              style={styles.canvas}
            />
          </div>

          {!isActive && (
            <div style={styles.placeholder}>
              <h2>Real-Time AR Pose Correction</h2>
              <p>Select a drill and click "Start Practice" to begin</p>
              <p style={styles.note}>
                ðŸ“¹ This feature uses your camera to provide live feedback on your form
              </p>
            </div>
          )}
        </div>
      </div>

      <div style={styles.instructions}>
        <h4>How it works:</h4>
        <ol>
          <li>Select a drill type from the left</li>
          <li>Click "Start Practice" to activate your camera</li>
          <li>Perform the movement slowly</li>
          <li>Watch for green (good) and red (needs improvement) indicators</li>
          <li>Follow the real-time feedback messages</li>
        </ol>
      </div>
    </div>
  )
}

const styles = {
  container: {
    minHeight: '100vh',
    backgroundColor: '#f5f5f5',
    padding: '1rem',
  },
  header: {
    display: 'flex',
    justifyContent: 'space-between',
    alignItems: 'center',
    marginBottom: '1rem',
  },
  title: {
    color: '#333',
    margin: 0,
  },
  backButton: {
    padding: '0.5rem 1rem',
    backgroundColor: '#2196F3',
    color: 'white',
    border: 'none',
    borderRadius: '4px',
    cursor: 'pointer',
  },
  content: {
    display: 'grid',
    gridTemplateColumns: '300px 1fr',
    gap: '1rem',
    marginBottom: '1rem',
  },
  sidebar: {
    display: 'flex',
    flexDirection: 'column',
    gap: '1rem',
  },
  sidebarTitle: {
    color: '#333',
    margin: 0,
  },
  drillCard: {
    padding: '1rem',
    backgroundColor: 'white',
    borderRadius: '8px',
    cursor: 'pointer',
    border: '2px solid transparent',
    transition: 'all 0.2s',
  },
  drillCardActive: {
    borderColor: '#4CAF50',
    backgroundColor: '#e8f5e9',
  },
  drillDesc: {
    margin: '0.5rem 0 0 0',
    fontSize: '0.9rem',
    color: '#666',
  },
  controls: {
    display: 'flex',
    flexDirection: 'column',
    gap: '0.5rem',
  },
  button: {
    padding: '0.75rem',
    color: 'white',
    border: 'none',
    borderRadius: '4px',
    fontSize: '1rem',
    fontWeight: 'bold',
    cursor: 'pointer',
  },
  feedbackPanel: {
    backgroundColor: '#fff3cd',
    padding: '1rem',
    borderRadius: '8px',
    borderLeft: '4px solid #ffc107',
  },
  feedbackList: {
    margin: '0.5rem 0 0 0',
    paddingLeft: '1.5rem',
  },
  cameraContainer: {
    position: 'relative',
    backgroundColor: 'black',
    borderRadius: '8px',
    overflow: 'hidden',
    minHeight: '500px',
  },
  videoWrapper: {
    position: 'relative',
    width: '100%',
    height: '100%',
  },
  video: {
    width: '100%',
    height: '100%',
    objectFit: 'cover',
    transform: 'scaleX(-1)', // Mirror for selfie view
  },
  canvas: {
    position: 'absolute',
    top: 0,
    left: 0,
    width: '100%',
    height: '100%',
    pointerEvents: 'none',
  },
  placeholder: {
    position: 'absolute',
    top: '50%',
    left: '50%',
    transform: 'translate(-50%, -50%)',
    textAlign: 'center',
    color: 'white',
    maxWidth: '80%',
  },
  note: {
    marginTop: '1rem',
    fontSize: '0.9rem',
    opacity: 0.8,
  },
  instructions: {
    backgroundColor: 'white',
    padding: '1.5rem',
    borderRadius: '8px',
    maxWidth: '800px',
    margin: '0 auto',
  },
}

import { useLanguage } from '../context/LanguageContext'

export default function LanguageSelector() {
  const { language, changeLanguage, availableLanguages } = useLanguage()

  return (
    <div style={styles.container}>
      <select
        value={language}
        onChange={(e) => changeLanguage(e.target.value)}
        style={styles.select}
      >
        {availableLanguages.map(lang => (
          <option key={lang.code} value={lang.code}>
            {lang.name}
          </option>
        ))}
      </select>
    </div>
  )
}

const styles = {
  container: {
    display: 'inline-block',
  },
  select: {
    padding: '0.5rem 1rem',
    borderRadius: '4px',
    border: '1px solid #ddd',
    backgroundColor: 'white',
    cursor: 'pointer',
    fontSize: '0.9rem',
  },
}

